import React, { useState } from "react";
import { Avatar, Button, Card, Col, Form, Input, Row } from "antd";
import { useAutoAnimate } from "@formkit/auto-animate/react";

const { Meta } = Card;

export default function DynamicCards() {
  const [comments, setComments] = useState([]);
  const [parent, enable] = useAutoAnimate({ duration: 500 });
  const [isEnabled, setIsEnabled] = useState(true);

  const handleSubmit = (values) => {
    if (!values) return;
    setComments((prev) => [{ content: values.content }, ...prev]);
  };

  function toggle() {
    enable(!isEnabled);
    setIsEnabled(!isEnabled);
  }

  return (
    <>
      <Form name="basic" onFinish={handleSubmit} autoComplete="off">
        <Form.Item name="content">
          <Input />
        </Form.Item>
        <Form.Item>
          <Button htmlType="submit" type="primary">
            Add Task
          </Button>
        </Form.Item>
        <Form.Item>
          <Button onClick={toggle} type="primary">
            {isEnabled ? "ðŸš« Disable" : "âœ… Enable"} animations
          </Button>
        </Form.Item>
      </Form>

      <Row gutter={[16, 24]} ref={parent}>
        {comments.map((comment) => (
          <Col span={6} key={comment.content}>
            <Card
              style={{
                width: 100
              }}
              cover={
                <img
                  alt="example"
                  src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                />
              }
            >
              <Meta
                avatar={<Avatar src="https://joeschmoe.io/api/v1/random" />}
                description={comment.content}
              />
            </Card>
          </Col>
        ))}
      </Row>
    </>
  );
}
